import { createSlice } from "@reduxjs/toolkit";

const mathSlice = createSlice({
   name:'mathSlice',
   initialState:{
      input1: '',
      input2: ''
   },
   reducers:{
      addNumbers
   }
})



export default mathSlice.reducer;
