import { createSlice } from "@reduxjs/toolkit";

const mathSlice = createSlice({
   name:'mathSlice',
   initialState:{
      input1: '',
      input2: ''
   },
   reducers:{
      plus:(state,action)=>{
         state.input1 = action.payload
      }
   }
})



export default mathSlice.reducer;
