import React from "react"
import { useDispatch, useSelector } from "react-redux"
import {firstInput, setInput2, addNumbers, subtractNumbers, multiplyNumbers, divideNumbers,} from "..//../store/mathSlice" 
import '..//../math.css'

function MathPage() {
  const dispatch = useDispatch()
  const { input1, input2, result } = useSelector((state) => state.mathSlice)

  const plus = () => {
    dispatch(addNumbers({ num1: input1, num2: input2 }))
  }

  const minus = () => {
    dispatch(subtractNumbers({ num1: input1, num2: input2 }))
  }

  const multiply = () => {
    dispatch(multiplyNumbers({ num1: input1, num2: input2 }))
  }

  const devide = () => {
    dispatch(divideNumbers({ num1: input1, num2: input2 }))
  }

  return (
   <div className="container">
      <div className="calculator">
         <div>
            <h1>calculator</h1>
            <input type="number" value={input1} onChange={(event) => dispatch(firstInput(event.target.value))}/>
            <input type="number" value={input2} onChange={(event) => dispatch(setInput2(event.target.value))}/>
            
         </div>
            
         <div className="buttons">
            <button onClick={plus}>+</button>
            <button onClick={minus}>-</button>
            <button onClick={multiply}>*</button>
            <button onClick={devide}>/</button>
         </div>
      </div>
            <p>answer: {result}</p>
   </div>
  )
}

export default MathPage;
