import React from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  setInput1,
  setInput2,
  addNumbers,
  subtractNumbers,
  multiplyNumbers,
  divideNumbers,
} from "./mathSlice"; // Импортируем созданные actions

function MathPage() {
  const dispatch = useDispatch();
  const { input1, input2, result } = useSelector((state) => state.mathSlice);

  const handleAdd = () => {
    dispatch(addNumbers({ num1: input1, num2: input2 }));
  };

  const handleSubtract = () => {
    dispatch(subtractNumbers({ num1: input1, num2: input2 }));
  };

  const handleMultiply = () => {
    dispatch(multiplyNumbers({ num1: input1, num2: input2 }));
  };

  const handleDivide = () => {
    dispatch(divideNumbers({ num1: input1, num2: input2 }));
  };

  return (
    <div>
      <h1>Math Calculator</h1>
      <input
        type="number"
        value={input1}
        onChange={(e) => dispatch(setInput1(e.target.value))}
      />
      <input
        type="number"
        value={input2}
        onChange={(e) => dispatch(setInput2(e.target.value))}
      />
      <button onClick={handleAdd}>+</button>
      <button onClick={handleSubtract}>-</button>
      <button onClick={handleMultiply}>*</button>
      <button onClick={handleDivide}>/</button>
      <p>Result: {result}</p>
    </div>
  );
}

export default MathPage;
